import React, { useState } from 'react';
import './SearchBar.css';

function SearchBar({ setSearchTerm, darkMode, setDarkMode }) {
  const [dropdownVisible, setDropdownVisible] = useState(false);

  const toggleDropdown = () => {
    setDropdownVisible(!dropdownVisible);
  };

  return (
    <div className="search-bar-container">
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search..."
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      <div className="dropdown-container">
        <button className="all-button" onClick={toggleDropdown}>All ‚ñº</button>
        {dropdownVisible && (
          <div className="dropdown-list">
            <button className="dropdown-item all-button">All</button>
            <button className="dropdown-item complete-button">Completed</button>
            <button className="dropdown-item incomplete-button">Incomplete</button>
          </div>
        )}
      </div>

      <button className="mode-button" onClick={() => setDarkMode(!darkMode)}>
        {darkMode ? 'üåô' : '‚òÄÔ∏è'}
      </button>
    </div>
  );
}

export default SearchBar;
