import React, { useState } from 'react';
import './SearchBar.css';
import { FaSearch } from 'react-icons/fa'; // Import the magnifying glass icon

function SearchBar({ setSearchTerm, darkMode, setDarkMode, handleFilterChange }) {
  const [dropdownVisible, setDropdownVisible] = useState(false);
  const [selectedFilter, setSelectedFilter] = useState('All');

  const handleFilterClick = (filter) => {
    setSelectedFilter(filter);
    handleFilterChange(filter);
    setDropdownVisible(false);
  };

  return (
    <div className="search-bar-container">
      <div className="search-bar">
        <FaSearch className="search-icon" /> {/* Magnifying glass icon */}
        <input
          type="text"
          placeholder="Search..."
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>
      <div className="dropdown-container">
        <button
          className="all-button"
          onClick={() => setDropdownVisible(!dropdownVisible)}
        >
          {selectedFilter}
        </button>
        {dropdownVisible && (
          <div className="dropdown-list">
            <button
              className="dropdown-item"
              onClick={() => handleFilterClick('All')}
            >
              All
            </button>
            <button
              className="dropdown-item"
              onClick={() => handleFilterClick('Completed')}
            >
              Completed
            </button>
            <button
              className="dropdown-item"
              onClick={() => handleFilterClick('Incomplete')}
            >
              Incomplete
            </button>
          </div>
        )}
      </div>
      <button className="mode-button" onClick={() => setDarkMode(!darkMode)}>
        {darkMode ? 'ðŸŒ™' : 'ðŸŒž'}
      </button>
    </div>
  );
}

export default SearchBar;
